package varaibles;

public class DoWhileExpple {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=9;
		
		while (a>=10) {
			System.out.println("While Loop Excecuted" +a);
		}
		
		System.out.println("========================");
		
		int b=0;
		do {
			System.out.println("Do While Loop Excecuted" +b);	
		}while(b>=9);
		
	}

}
