package varaibles;

public class IngrementOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=45;
		x +=5;
		System.out.println("X = " +x);
		x -=5;
		System.out.println("X = " +x);
		
		x *=5;
		System.out.println("X = " +x);
		
		x /=5;
		System.out.println("X = " +x);
		
		System.out.println("X = " + x++);
		
		System.out.println("X = " + ++x) ;
	}

}
